mapper.insert:
INSERT INTO t_feed
SET iuser = #{iuser}
, contents = #{contents}
, location = #{location}

mapper.select:
SELECT B.ifeed, B.contents, B.location, B.created_at as createdAt ,A.iuser as writerIuser, A.nm as writerNm, A.pic as writerPic ,IF(C.ifeed>0,1,0) as isFav FROM t_user A JOIN t_feed B ON A.iuser = B.iuser
JOIN t_feed_fav C
left JOIN t_feed_fav C
ON B.ifeed = C.ifeed AND C.iuser = #{loginedIuser}
WHERE B.iuser = #{targetIuser}
ORDER BY B.ifeed DESC LIMIT #{startIdx}, #{rowCount}

mapper.delete:
DELETE
FROM t_feed
WHERE ifeed = #{ifeed}
AND iuser = #{iuser}

mapper.select:
SELECT ifeed
FROM t_feed
WHERE ifeed = #{ifeed}
AND iuser = #{iuser}

mapper.delete:
DELETE A
FROM t_feed B
join t_feed_pics A
ON A.ifeed = B.ifeed
WHERE ifeed = #{ifeed}
AND iuser = #{iuser}
