mapper.insert:
INSERT INTO t_user
SET provider_type = #{providerType}
, uid = #{uid}
, upw = #{upw}
, nm = #{nm}
, role = #{role}

    ,pic = #{pic}

mapper.select:
SELECT iuser,uid, upw, nm, pic, role
, created_at as createdAt, updated_at as updatedAt
, firebase_token as firebaseToken
FROM t_user


        provider_type = #{providerType} AND uid = #{uid}


        iuser = #{iuser}

mapper.delete:
DELETE
FROM t_user_follow
WHERE from_iuser =#{fromIuser}
AND to_iuser = #{toIuser}

mapper.update:
UPDATE t_user
SET firebase_token = #{firebaseToken}
WHERE iuser = #{iuser}

update t_user
set pic=#{pic}
where iuser=#{iuser}
